import { create } from "ipfs-http-client";

// Infura veya kendi IPFS düğümünü kullanabilirsin.
const ipfs = create({ host: "ipfs.infura.io", port: 5001, protocol: "https" });

export const uploadToIPFS = async (data) => {
  try {
    const file = await ipfs.add(JSON.stringify(data));
    return file.path; // IPFS Hash
  } catch (error) {
    console.error("IPFS Yükleme Hatası:", error);
    return null;
  }
};

export const fetchFromIPFS = async (hash) => {
  try {
    const response = await fetch(`https://ipfs.io/ipfs/${hash}`);
    const data = await response.json();
    return data;
  } catch (error) {
    console.error("IPFS Veri Çekme Hatası:", error);
    return null;
  }
};
