import { Web3Storage } from "web3.storage";

const WEB3_STORAGE_API_KEY = "z6Mkna8ipXeTqeE4GmAf1Tz3tXwtXdKyt6YZzjDuQ3xzGuFP"; // API Key buraya

export const uploadToIPFS = async (data) => {
  try {
    const client = new Web3Storage({ token: WEB3_STORAGE_API_KEY });
    const blob = new Blob([JSON.stringify(data)], { type: "application/json" });
    const file = new File([blob], "data.json");

    const cid = await client.put([file]); // IPFS CID al
    return `https://dweb.link/ipfs/${cid}`; // CID’den URL oluştur
  } catch (error) {
    console.error("Web3.Storage yükleme hatası:", error);
    return null;
  }
};
