import React, { useState, useEffect } from "react";
import { loginWithGoogle, logout, checkAuthState } from "./auth";
import { db } from "./firebase"; // Firestore bağlantısı
import { doc, getDoc } from "firebase/firestore";
import { uploadToIPFS } from "./pinata"; // Pinata ile IPFS yükleme fonksiyonu

const App = () => {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [tcKimlik, setTcKimlik] = useState("");
  const [notlar, setNotlar] = useState("");
  const [ogrenciNot, setOgrenciNot] = useState("");
  const [hash, setHash] = useState("");

  useEffect(() => {
    checkAuthState(async (loggedUser) => {
      setUser(loggedUser);
      if (loggedUser) {
        await getUserRole(loggedUser.uid);
      }
    });
  }, []);

  const getUserRole = async (uid) => {
    const userRef = doc(db, "users", uid);
    const userSnap = await getDoc(userRef);
    if (userSnap.exists()) {
      setRole(userSnap.data().role);
    } else {
      setRole("student");
    }
  };

  const handleAddGrade = async () => {
    if (!user) return alert("Önce giriş yapın!");

    const data = { tcKimlik, notlar };

    try {
      const ipfsHash = await uploadToIPFS(JSON.stringify(data));

      if (!ipfsHash) {
        alert("Veri yükleme başarısız! Lütfen tekrar deneyin.");
        return;
      }

      setHash(ipfsHash);
      alert(`Veriler başarıyla IPFS'e yüklendi! Hash: ${ipfsHash}`);
    } catch (error) {
      console.error("Not kaydetme hatası:", error);
      alert("Bir hata oluştu, lütfen tekrar deneyin.");
    }
  };

  return (
    <div>
      {user ? (
        <>
          <h2>Hoş geldin, {user.displayName}</h2>
          <button onClick={logout}>Çıkış Yap</button>

          {role === "teacher" && (
            <div>
              <h3>Not Ekle</h3>
              <input
                type="text"
                placeholder="Öğrenci TC Kimlik No"
                value={tcKimlik}
                onChange={(e) => setTcKimlik(e.target.value)}
              />
              <input
                type="text"
                placeholder="Notları virgülle ayırarak gir (örn: 90,85,78)"
                value={notlar}
                onChange={(e) => setNotlar(e.target.value)}
              />
              <button onClick={handleAddGrade}>Notu Kaydet</button>
            </div>
          )}

          <div>
            <h3>Notları Gör</h3>
            <input
              type="text"
              placeholder="TC Kimlik No"
              value={tcKimlik}
              onChange={(e) => setTcKimlik(e.target.value)}
            />
            <button>Notları Göster</button>
            {ogrenciNot && <p>Notlar: {ogrenciNot}</p>}
          </div>
        </>
      ) : (
        <button onClick={loginWithGoogle}>Google ile Giriş Yap</button>
      )}
    </div>
  );
};

export default App;
