import axios from "axios";

// Pinata API JWT Token
const PINATA_JWT = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..FB9CVBoIxlvoXZqA9oXbTBtw4VlcY1atRjAp0t4Qug8"; // Pinata'dan aldığın JWT Token

export const uploadToIPFS = async (data) => {
  try {
    const res = await axios.post(
      "https://api.pinata.cloud/pinning/pinJSONToIPFS",
      { pinataContent: data },
      {
        headers: {
          "Content-Type": "application/json",
          Authorization: PINATA_JWT,
        },
      }
    );
    return res.data.IpfsHash; // IPFS Hash'i döndür
  } catch (error) {
    console.error("Pinata yükleme hatası:", error);
    return null;
  }
};

export const fetchFromIPFS = async (hash) => {
  try {
    const res = await axios.get(`https://gateway.pinata.cloud/ipfs/${hash}`);
    return res.data;
  } catch (error) {
    console.error("IPFS veri çekme hatası:", error);
    return null;
  }
};
