import axios from "axios";

const PINATA_JWT = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..3X9TyPbUkRe7BnW5K8LHMUf1vy1qYC7r1x2dEqFQW1A"; // Buraya kendi JWT'nizi ekleyin

export const uploadToIPFS = async (data) => {
  try {
    const res = await axios.post(
      "https://api.pinata.cloud/pinning/pinJSONToIPFS",
      { pinataContent: data },
      {
        headers: {
          "Content-Type": "application/json",
          Authorization: PINATA_JWT,
        },
      }
    );
    return res.data.IpfsHash;
  } catch (error) {
    console.error("IPFS yükleme hatası:", error);
    return null;
  }
};

export const fetchFromIPFS = async (hash) => {
  try {
    const res = await axios.get(`https://gateway.pinata.cloud/ipfs/${hash}`);
    return res.data;
  } catch (error) {
    console.error("IPFS veri çekme hatası:", error);
    return null;
  }
};
