import React, { useState, useEffect } from "react";
import { loginWithGoogle, logout, checkAuthState } from "./auth";
import { db } from "./firebase"; 
import { doc, getDoc } from "firebase/firestore";

const App = () => {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState("");

  useEffect(() => {
    checkAuthState(async (loggedUser) => {
      setUser(loggedUser);
      if (loggedUser) {
        await getUserRole(loggedUser.uid);
      }
    });
  }, []);

  const getUserRole = async (uid) => {
    try {
      const userRef = doc(db, "users", uid);
      const userSnap = await getDoc(userRef);
      if (userSnap.exists()) {
        setRole(userSnap.data().role);
      } else {
        setRole("student");
      }
    } catch (error) {
      console.error("Rol çekme hatası:", error);
    }
  };

  return (
    <div>
      {user ? (
        <>
          <h2>Hoş geldin, {user.displayName}</h2>
          <button onClick={logout}>Çıkış Yap</button>

          {role === "teacher" ? (
            <div>
              <h3>Not Ekle</h3>
              <input type="text" placeholder="Öğrenci TC Kimlik No" />
              <input type="text" placeholder="Notları virgülle gir (örn: 90,85,78)" />
              <button>Notu Kaydet</button>
            </div>
          ) : (
            <h3>Öğretmen Girişi Gereklidir</h3>
          )}
        </>
      ) : (
        <button onClick={loginWithGoogle}>Google ile Giriş Yap</button>
      )}
    </div>
  );
};

export default App;
